INSERT INTO films (id,title,premier_year,description,created_by,created_when,update_by,update_when,is_deleted,deleted_by,deleted_when,image_url_link) VALUES
	 (1,'Маленькие секреты',2010,'История друзей, которые по традиции отправляются провести совместный летний отдых на мыс Кап-Ферре неподалеку от Ниццы. Но когда они начинают исповедоваться друг другу в своих грехах, атмосфера становится довольно напряженной.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://ru.wikipedia.org/wiki/%D0%9C%D0%B0%D0%BB%D0%B5%D0%BD%D1%8C%D0%BA%D0%B8%D0%B5_%D1%81%D0%B5%D0%BA%D1%80%D0%B5%D1%82%D1%8B_%28%D1%84%D0%B8%D0%BB%D1%8C%D0%BC%29#/media/%D0%A4%D0%B0%D0%B9%D0%BB:%D0%9F%D0%BE%D1%81%D1%82%D0%B5%D1%80_%D1%84%D0%B8%D0%BB%D1%8C%D0%BC%D0%B0_%C2%AB%D0%9C%D0%B0%D0%BB%D0%B5%D0%BD%D1%8C%D0%BA%D0%B8%D0%B5_%D1%81%D0%B5%D0%BA%D1%80%D0%B5%D1%82%D1%8B%C2%BB.jpg'),
	 (22,'Соединённые Штаты против Билли Холидей',2021,'1940 год. Популярная певица Билли Холидей становится мишенью правительства США из-за пропаганды равноправия в своих песнях. Чиновники решают бороться с ней при помощи отдела по борьбе с наркотиками — операцию возглавляет федеральный агент Джимми Флетчер.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://img02.rl0.ru/afisha/e920x1380q65i/s2.afisha.ru/mediastorage/81/98/1b6d8aba5c5f4a86a28444d99881.jpg'),
	 (26,'Несломленный',2014,'Американский легкоатлет Луи Замперини участвовал в Олимпийских играх 1936 года в Берлине. Он не одержал победы в финальном забеге, но снискал расположение Гитлера, который пригласил его к себе в ложу. Предполагалось, что Замперини примет участие и в следующих Олимпийских играх, но из-за начала Второй мировой войны он был вынужден отправиться на фронт. Его самолет потерпел крушение над Тихим океаном, после чего Замперини вместе с двумя сослуживцами провёл 47 дней в дрейфе на плоту и попал в плен к японцам.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://upload.wikimedia.org/wikipedia/ru/7/76/Unbroken_poster.jpg'),
	 (25,'Тоня против всех',2017,'Американской фигуристке Тоне Хардинг пришлось нелегко: сначала тяжелое детство с грозной матерью, потом тяжелая юность, ранний брак с проходимцем и неудачи на соревнованиях из-за заниженных судьями оценок. А потом случился скандал: во время важнейших соревнований план запугать конкурентку выходит Тоне боком.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://upload.wikimedia.org/wikipedia/ru/d/d3/I%2C_Tonya.jpg'),
	 (3,'Меланхолия',2011,'События фильма разворачиваются в дни, которые предшествуют катастрофе. Первая часть посвящена свадьбе Жюстин, которая быстро охладевает к торжеству, чем вызывает непонимание близких и гостей. Героиней второй части является Клэр, сестра Жюстин. Вначале Клэр ухаживает за впавшей в клиническую депрессию Жюстин и одновременно страшится сообщений о приближении таинственной планеты Меланхолия. Постепенно, по мере приближения планеты, Жюстин и Клэр меняются ролями. Теперь паникующая Клэр нуждается в заботе. В отчаянии она с сестрой и сыном готовится принять неизбежное.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://ru.wikipedia.org/wiki/%D0%9C%D0%B5%D0%BB%D0%B0%D0%BD%D1%85%D0%BE%D0%BB%D0%B8%D1%8F_%28%D1%84%D0%B8%D0%BB%D1%8C%D0%BC,_2011%29#/media/%D0%A4%D0%B0%D0%B9%D0%BB:MelancholiaPoster.jpg'),
	 (49,'Красотка на взводе',2021,'С самого детства Линди страдает от неконтролируемых вспышек агрессии, вернее — страдают окружающие. Почти всю жизнь девушка провела во всевозможных клиниках, изучалась различными специалистами, и единственное, что помогает ей не калечить раздражителей — специальный жилет из электродов, который бьёт её током по нажатию кнопки. Однажды Линди встречает хорошего парня, мгновенно влюбляется и понимает, что жизнь может быть прекрасной без электрических разрядов, но кто-то неосмотрительно убивает её возлюбленного. Теперь всем причастным и просто случайным прохожим сильно не поздоровится.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://cdn-irec.r-99.com/sites/default/files/product-images/1330146/pnPaKIjQhqODN4cLwByw1w.jpeg'),
	 (56,'Гонки «Пушечное ядро»',1981,'Приз в кругленькую сумму получит тот из участников транснациональной автомобильной гонки, кто первым придет к финишу. Гонки, как обычно, без правил, и участвуют в них самые отчаянные и бесшабашные гонщики мира. Главное - стать первым любой ценой.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://upload.wikimedia.org/wikipedia/ru/2/20/Cannonball_run_poster.jpg'),
	 (55,'Паровоз Генерал',1926,'Юного Джонни Грэя в армию Конфедерации не взяли, а его возлюбленная Аннабел Ли отвергает его, обозвав трусом. Тем не менее, он со своим «напарником» паровозом утирает всем нос, выигрывая сражение. Джонни воюет с угонщиками поезда, непослушной пушкой и непредсказуемой силой судьбы.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://b1.filmpro.ru/c/20035.jpg'),
	 (14,'Пленный',2008,'Кавказская война. Чеченцы перекрыли дорогу, и федералам нужно заплатить за проезд. Денег или патронов нет, подкрепления ждать неоткуда, поэтому двое бывалых рядовых-старослужащих ловят пленного, чтобы им расплатиться за проезд. Груз, впрочем, оказывается более ценным и опасным, чем они думали.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://b1.filmpro.ru/c/244187.jpg'),
	 (11,'Человек-амфибия',1961,'В приморском городке давно ходят слухи о "морском дьяволе". Бизнесмен Зурита не верит им, пока сам не сталкивается с ним при драматических обстоятельствах. А столкнувшись, ставит перед собой цель - заполучить "дьявола" себе.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://upload.wikimedia.org/wikipedia/ru/d/d7/%D0%9F%D0%BB%D0%B0%D0%BA%D0%B0%D1%82_%D0%BA_%D1%84%D0%B8%D0%BB%D1%8C%D0%BC%D1%83_%C2%AB%D0%A7%D0%B5%D0%BB%D0%BE%D0%B2%D0%B5%D0%BA-%D0%B0%D0%BC%D1%84%D0%B8%D0%B1%D0%B8%D1%8F%C2%BB.jpg'),
	 (33,'Кража в музее',2008,'Сюжет комедии разворачивается вокруг трех охранников музея, которые разрабатывают план кражи картин, полюбившихся им за годы работы, но которые хотят передать в другой музей.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://static.hdrezka.ac/i/2015/6/22/ief8afc19d4c7if81b23d.jpg'),
	 (2,'Между прошлым и будущим',2021,'Саманта, в прошлом популярная певица, стоит на жизненном распутье. Законченная карьера, больной отец, накопивший кучу хлама, который собирал всю жизнь, последние творческие неудачи — все это погружает ее в депрессию. Но ироничное отношение к себе и к окружающим проблемам позволяет Саманте найти причины для новой жизни.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://www.film.ru/sites/default/files/movies/posters/50240567-2076389.jpg'),
	 (5,'Так сказал Чарли',2018,'Молодой аспирант пишет работу о серии убийств, совершенных девушками из секты «Семья». Основателем этой банды был Чарльз Мэнсон, приговоренный к смертной казни. Находясь под властью преступника, обладающего гипнотической притягательностью, обычные девушки были готовы совершить любые преступления. Аспирант посещает тюрьму, где отбывают пожизненное заключение три девушки, члены секты.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://img06.rl0.ru/afisha/e920x1380q65i/s5.afisha.ru/mediastorage/84/4e/c76c399ff2f748d08cf83f754e84.jpg'),
	 (24,'Легенда №17',2012,'История жизни хоккеиста Валерия Харламова.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://upload.wikimedia.org/wikipedia/ru/a/a0/%D0%9F%D0%BE%D1%81%D1%82%D0%B5%D1%80_%D1%84%D0%B8%D0%BB%D1%8C%D0%BC%D0%B0_%C2%AB%D0%9B%D0%B5%D0%B3%D0%B5%D0%BD%D0%B4%D0%B0_%E2%84%9617%C2%BB.jpg'),
	 (45,'Свистуны',2019,'Кристи, коррумпированный полицейский из Бухареста, без памяти влюбляется в прекрасную Гилду, роковую женщину, связанную с преступным бизнесом. Ради нее он готов на любую авантюру. Кристи намерен отправиться на солнечный остров Гомера, чтобы вызволить из тюрьмы нечистого на руку бизнесмена. Но для осуществления дерзкого плана он должен выучить местный язык свиста, специально разработанный бандитами для общения на расстоянии.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://img08.rl0.ru/afisha/e920x1380q65i/s.afisha.ru/mediastorage/e0/c6/213d97c9734d428694987fa5c6e0.jpg'),
	 (41,'Трио',2002,'Трое оперативников из российской глубинки Николай, Алексей и Марина осуществляют секретную операцию по ликвидации банды, грабящей и сжигающей автофуры. Но не только профессиональный долг заставляет героев бесконечно колесить по дорогам. Поиски бандитов осложняются непростыми личными отношениями двух мужчин и одной женщины…',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://upload.wikimedia.org/wikipedia/ru/e/e9/%D0%A2%D1%80%D0%B8%D0%BE%2C_%D0%BF%D0%BE%D1%81%D1%82%D0%B5%D1%80.jpg'),
	 (44,'Форсаж 4',2009,'Когда кривая дорожка заставляет скрывающегося от правосудия Дома Торетто вернуться в Лос-Анджелес, вражда с агентом Брайаном О’Коннером вспыхивает с новой силой. Но у парочки обнаруживается общий противник, и Дому с Брайаном приходится заключить перемирие в надежде одержать над ним победу. Грабя конвои, роя подкопы и пересекая границы, они приходят к идеальной форме отмщения — педали газа, вдавленной до упора.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://static.kinoafisha.info/k/movie_posters/1080x1920/upload/movie_posters/9/1/1/5123119/d6c7b9bf3fccfe0d7aa4aafc1656312a.jpg'),
	 (57,'Повелитель теней',2022,'Главный герой детективного сериала «Повелителей теней» – полицейский Рассел, который должен расследовать новое дело на грани полного безумия. Где-то в американской глубинке находится заброшенная психиатрическая лечебница. Ночной сторож Анна бродит по коридорам и наблюдает за живущими там бездомными, которые облюбовали это место. Вскоре Анна осознает, что те являются жертвами какого-то культа, похищают детей и проводят непонятные жуткие ритуалы. Она пытается остановить сектантов, терпит поражение, но, не готовая сдаваться, заключает фаустовскую сделку с богом смерти Хануманом. Теперь женщина получает неограниченную силу, но каждый раз, используя ее, отдает часть своей души. Сможет ли детектив Рассел распутать этот клубок таинственных событий?',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://x-film.ws/uploads/posts/2022-11/1667929120-768178866-povelitel-tenej.jpg'),
	 (47,'Из Парижа с любовью',2009,'Джеймсу можно позавидовать: должность личного помощника посла США во Франции, красавица-парижанка в качестве подружки, дорогие костюмы и непыльная работёнка оперативника ЦРУ. Но мечтает он совсем о другом — о полной приключений жизни настоящего тайного агента, спасающего мир кулаками, выстрелами и едкими репликами. Судьба иронично улыбается Джеймсу. Он получает настоящее боевое задание в придачу с напарником-сорвиголовой Чарли Уэксом. Но вскоре он понимает, что быть суперагентом не так уж и здорово, как он представлял.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://likefilmdb.ru/static/images/film/1/309.jpg'),
	 (52,'Акула: Начало',2021,'Старшеклассник Чха У Соль — мишень для школьных издевательств. Однажды жестоко дав отпор, он попадает в тюрьму, где встречает чемпиона по боям без правил. Тот соглашается показать ему лучшие приемы самообороны.',NULL,NULL,NULL,NULL,false,NULL,NULL,'https://cdn-irec.r-99.com/sites/default/files/product-images/2274752/rSGX4M8YPRTQzkITNt47A.jpg'),
	 (51,'Жажда золота',2021,'Будущее. Ресурсы на исходе. Люди выживают, как могут. Два попутчика отправляются в опасную зону — на заброшенный прииск в пустыне. Когда они находят огромные залежи золота, более опытный искатель отправляется за техникой, а молодой остаётся караулить сокровище. Приближается песчаная буря, вокруг снуют дикие животные и опасные путники, а напарник не выходит на связь.
